%dw 2.0
output application/json

//Code assumes there are three payloads saved as variables:
//  -> flightsPayload
//  -> transactionsPayload
//  -> accountsPayload

//Create a function to take care of building the Transaction objects
//End result should be an array of Transaction objects with a new structure according to the Transaction datatype in the RAML spec
fun createNewTransactions(transactions) =
	transactions map ((transaction) ->

		//Assign a local variable within the map scope by utilizing the "using" operator
		using (account = (vars.accountsPayload filter ($.accountID == transaction.customerRef))[0])
		(	
			//Code transformation here for each transaction object
			transaction : {
				idCustomer: transaction.customerRef,
				nameCustomer: account.name,
				'type': account.accountType,
				amount: transaction.amount,
				flightID: transaction.flightID,
				transID: transaction.transactionID
			}
		)
	)
---

using (filteredTransactions = createNewTransactions(vars.transactionsPayload)) {
	//Filter the transactions according to the unique Flight ID in vars.transactionsPayload
	flights: using (flights=  vars.flightsPayload map(object, index) ->

			flight: {
				flightCode: object.flightCode,
				airline: object.airlineName,
				departureDate: object.departureDate as Date {format: "yyyy/MM/dd"} as String  {format: "yyyy-MMM-dd"},
				emptySeats: object.availableSeats as Number,
				fromAirportCode: object.origination,
				planeType: object.planeType,
				price: object.price as Number,
				toAirportCode: object.destination,		
				transactions: using(flightID = upper(object.airlineName ++ object.flightCode 
																++ (object.departureDate as Date {format: "yyyy/MM/dd"} as String  {format: "yyyy-MM-dd"})))																															
							  filteredTransactions  filter ((value, index) -> (value.transaction.flightID == flightID))							  
				}			

)
flights
}

