output application/xml
import getAmountUSFormat from moludes::Formats
---

flights: {( payload..*flight map ((flight , flightIndex) -> {
	flight @(flightCode: flight.flightCode as String): {
		airline: flight.airline as String,
		departureDate: flight.departureDate,
		emptySeats: flight.emptySeats,
		fromAirportCode: flight.fromAirportCode as String,
		planeType: flight.planeType as String,
		price: flight.price,
		toAirportCode: flight.toAirportCode as String,
		transactions: {(flight..*transaction default [] map ((transaction , transactionIndex) -> {
			transaction  @(transID: transaction.transID as String, amount: transaction.amount): {
				idCustomer: transaction.idCustomer,
				nameCustomer: transaction.nameCustomer,
				'type': transaction.'type',
				amountUSFormatted: transaction.amount	as Number {format: "#,###.00"},	
				amountUSFormattedUsingFun: getAmountUSFormat(transaction.amount)		
			}
			}
		))}
	}})
)}

